// src/app/components/ImageOptimization.tsx
import Image from 'next/image'

interface OptimizedImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
  priority?: boolean
}

export default function OptimizedImage({
  src,
  alt,
  width,
  height,
  className = '',
  priority = false
}: OptimizedImageProps) {
  return (
    <Image
      src={src || '/images/placeholder.jpg'}
      alt={alt}
      width={width}
      height={height}
      className={className}
      priority={priority}
      placeholder="blur"
      blurDataURL="data:image/jpeg;base64,"
      onError={(e) => {
        e.currentTarget.src = '/images/placeholder.jpg'
      }}
    />
  )
}